CREATE TABLE quantbot.ohlcv_candles\n(\n    `token_address` String,\n    `chain` String,\n    `timestamp` DateTime,\n    `interval` UInt32,\n    `open` Float64,\n    `high` Float64,\n    `low` Float64,\n    `close` Float64,\n    `volume` Float64\n)\nENGINE = MergeTree\nPARTITION BY (chain, toYYYYMM(timestamp))\nORDER BY (token_address, chain, timestamp)\nSETTINGS index_granularity = 8192
